<?php global $marketeer_data; $postmeta = get_post_custom(get_the_id()); if(isset($postmeta["link_post_url"][0])){	$link = $postmeta["link_post_url"][0];} else {	$link = "#";}?>	<div class="topBlog">			<div class="blog-category"><?php echo '<em>' . get_the_category_list( esc_html__( ', ', 'marketeer-lite' ) ) . '</em>'; ?> </div>		<h2 class="title"><a href="<?php echo esc_url($link); ?>" rel="bookmark" title="Permanent Link to <?php the_title_attribute(); ?>"><?php the_title(); ?></a></h2>		<?php if(isset($marketeer_data['display_post_meta'])) { ?>		<div class = "post-meta">			<?php 			$day = get_the_time('d');			$month= get_the_time('m');			$year= get_the_time('Y');			?>			<?php echo '<a class="post-meta-time" href="'.get_day_link( $year, $month, $day ).'">'; ?><?php echo get_the_date() ?></a><a href="<?php echo the_permalink() ?>#commentform"><?php comments_number(); ?></a>				</div>		<?php } ?> <!-- end of post meta -->	</div>		<div class="entry">		<div class = "meta">			<div class="blogContent">				<div class="blogcontent"><?php the_excerpt(); ?> </div>			</div>		</div>			</div>	